/// <reference types="@sapui5/ts-types" />
/**
 * Action
 * @isViewNode true
 */
export interface Action {
    /**
     * The text of the button tooltip, preferably as an i18n key.
     * @i18nClassification ACT: Text for tooltip of button.
     */
    tooltip?: string;
    /**
     * Icon to be displayed as graphical element within the Button. Can be an image or icon from the icon font, represented as RFC 3986 conformant URI.
     */
    icon?: sap.ui.core.URI;
    /**
     * Alternative icon for the active (depressed) state of the button, represented as RFC 3986 conformant URI.
     */
    activeIcon?: sap.ui.core.URI;
    /**
     * Defines the Button type.
     */
    type?: sap.m.ButtonType;
}
/**
 * Form Action
 * @isViewNode true
 */
export interface FormAction extends Action {
    /**
     * Whether the button should be visible on the screen.
     */
    visible?: boolean;
}
/**
 * Header Action
 * @isViewNode true
 */
export interface HeaderAction extends Action {
    /**
     * Whether the button should be visible on the screen.
     */
    visible?: boolean;
}
/**
 * ToolBar Action
 * @isViewNode true
 */
export interface ObjectPageToolBarAction extends Action {
    /**
     * Whether the button should be visible on the screen.
     */
    visible?: boolean;
}
